    private void OKBtnActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_OKBtnActionPerformed
        // TODO add your handling code here:
        if (valEmail(EmailTxt.getText()) == false) {
            ErrorLabel.setForeground(Color.RED);
            ErrorLabel.setText("Not a correct email try again.");
        }else{
            
            String newPW = passwordGen();
            
            if (changePassword(EmailTxt.getText() , newPW)==false) {
                ErrorLabel.setForeground(Color.BLUE);
                ErrorLabel.setText("Password send to Email!");
            }else{
                ErrorLabel.setForeground(Color.BLUE);
                sendPassword(EmailTxt.getText(), newPW);
                ErrorLabel.setText("Password send to Email!");
            }
            
        }
    }//GEN-LAST:event_OKBtnActionPerformed

  

