    private static String passwordGen() {
        service.LoginWebService_Service service = new service.LoginWebService_Service();
        service.LoginWebService port = service.getLoginWebServicePort();
        return port.passwordGen();
    }

    private static Boolean changePassword(java.lang.String arg0, java.lang.String arg1) {
        service.LoginWebService_Service service = new service.LoginWebService_Service();
        service.LoginWebService port = service.getLoginWebServicePort();
        return port.changePassword(arg0, arg1);
    }

    private static void sendPassword(java.lang.String arg0, java.lang.String arg1) {
        service.LoginWebService_Service service = new service.LoginWebService_Service();
        service.LoginWebService port = service.getLoginWebServicePort();
        port.sendPassword(arg0, arg1);
    }

    private static boolean valEmail(java.lang.String arg0) {
        service.LoginWebService_Service service = new service.LoginWebService_Service();
        service.LoginWebService port = service.getLoginWebServicePort();
        return port.valEmail(arg0);
    }
